function GetData(a,b,c){$.ajax({url:a,data:b,type:"GET",dataType:"json",success:c,error:function(a){console.log("err")}})}function ApplyPetitionToDOM(a,b,c){if(document.getElementById("loader").style.display="none","success"==b){for(var d=["action","background","state","signature_count","created_at","government_response_at","creator_name","government_response"],e=0;e<d.length;e++){var f=document.getElementById(d[e]);null!=f&&(f.innerHTML=a.data.attributes[d[e]])}var g=document.getElementById("state");g.innerHTML=g.innerHTML.toUpperCase(),"OPEN"==g.innerHTML?g.className+=" openState":g.className+=" closedState"}else console.warn("ajax call was successful however status was not successful")}$(document).ready(function(){GetData("https://petition.parliament.uk/petitions/131215.json",null,ApplyPetitionToDOM)});